import{_ as s,c as a,o as i,ag as o}from"./chunks/framework.C_7JoTpM.js";const n="/2025-Team-Epibot-Docs/videos/mechanic_videos/week1/gripper_simulation.mp4",r="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/jack.png",c="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/nut.png",p="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/circlips.png",l="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/body.png",m="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/jack_end.png",g="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/Screw_M5*25.png",h="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/axes.png",d="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/connecting_rod.png",_="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/circlip.png",b="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/Jaws.png",y="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/open_solidworks.jpeg",u="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/select_gripper_assembly.jpeg",k="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/insertion.jpeg",f="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/after_inserting.jpeg",w="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/Jaws_fixing_plan.jpeg",D="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/Jaws_contraint.png",t="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/cavity_plane_face.png",T="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/srew_head_base.png",q="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/assembly_jack-link_plan.jpeg",E="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/axial_ends.png",x="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coaxiality_1.jpeg",A="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coincidence_1.jpeg",v="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coincidence_2.jpeg",j="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coaxiality_2.jpeg",S="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/four_result.jpeg",J="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/parralellisme_contrainst.jpeg",P="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/A1_plan_1.png",B="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/A1_plan_2.png",M="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coaxiality_3.jpeg",C="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coincidence_3.jpeg",G="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/result_2.jpeg",L="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/A1_plan.png",I="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/A2_plan.png",F="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coaxiality_4.jpeg",N="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coaxiality_5.jpeg",O="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coaxiality_6.jpeg",W="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/coaxiality_7.jpeg",H="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/final_result_1.jpeg",V="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/symetrie_plan.jpeg",$="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/symetry_contrainst_1.png",Z="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/symetry_contrainst_2.jpeg",z="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/symetry_contrainst_3.jpeg",K="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/symetry_contrainst_3.png",R="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/Mass_finding.jpeg",X="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/error_type_1.png",Y="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/error_type_2.jpeg",U="/2025-Team-Epibot-Docs/images/mechanic_images/week1/pince/error_type_3.jpeg",re=JSON.parse('{"title":"Documentation: Assembly of the Mechanical Gripper","description":"","frontmatter":{},"headers":[],"relativePath":"week1/pole-mecanic/MechanicalGripper.md","filePath":"week1/pole-mecanic/MechanicalGripper.md"}'),Q={name:"week1/pole-mecanic/MechanicalGripper.md"};function ee(te,e,se,ae,ie,oe){return i(),a("div",null,e[0]||(e[0]=[o('<h1 id="documentation-assembly-of-the-mechanical-gripper" tabindex="-1"><span style="color:#2E86C1;"><strong>Documentation: Assembly of the Mechanical Gripper</strong></span> <a class="header-anchor" href="#documentation-assembly-of-the-mechanical-gripper" aria-label="Permalink to &quot;&lt;span style=&quot;color: #2E86C1;&quot;&gt;**Documentation: Assembly of the Mechanical Gripper**&lt;/span&gt;&quot;">​</a></h1><hr><h3 id="short-demo" tabindex="-1"><span style="color:#28B463;"><strong>Short Demo</strong></span> <a class="header-anchor" href="#short-demo" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Short Demo**&lt;/span&gt;&quot;">​</a></h3><video width="320" height="240" controls><source src="'+n+'" type="video/mp4"> Votre navigateur ne supporte pas la balise vidéo. </video> --- <h3 id="link-to-download-link-to-download-gripper" tabindex="-1"><span style="color:#28B463;"><strong>Link to Download</strong></span>: <a href="/pieces/week1/assemblage_pince.SLDASM" download><strong>Link to Download Gripper</strong></a> <a class="header-anchor" href="#link-to-download-link-to-download-gripper" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Link to Download**&lt;/span&gt;: &lt;a href=&quot;/pieces/week1/assemblage_pince.SLDASM&quot; download&gt;**Link to Download Gripper**&lt;/a&gt;&quot;">​</a></h3><h2 id="introduction" tabindex="-1"><span style="color:rgb(231, 60, 137);"><strong>Introduction</strong></span> <a class="header-anchor" href="#introduction" aria-label="Permalink to &quot;&lt;span style=&quot;color:rgb(231, 60, 137);&quot;&gt;**Introduction**&lt;/span&gt;&quot;">​</a></h2><p>The challenge is to assemble a mechanical gripper based on precise assembly plans while respecting dimensions and geometric constraints. The assembly consists of several separate parts, each playing a specific role in the overall operation of the gripper. These parts include:</p><hr><h3 id="key-components" tabindex="-1"><span style="color:#28B463;"><strong>Key Components</strong></span> <a class="header-anchor" href="#key-components" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Key Components**&lt;/span&gt;&quot;">​</a></h3><ul><li><p><strong><span style="color:#5DADE2;">Jack</span></strong>: The main actuator that allows the gripper to open and close.<br><img src="'+r+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">M4 Nut</span></strong>: Mechanically holds the elements together.<br><img src="'+c+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">W4 circlips</span></strong>: Distributes the pressure of the screws to prevent damage to the surfaces.<br><img src="'+p+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">Body</span></strong>: The main structure of the gripper, serving as support for all other parts.<br><img src="'+l+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">Jack End</span></strong>: Connector between the Jack and the links.<br><img src="'+m+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">M5x25 Socket Head Screw</span></strong>: Provides a solid fixation of the elements, particularly to secure the Jack end.<br><img src="'+g+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">Link Pin and Jaw Holder</span></strong>: Allows the rotation of the links and jaw holders.<br><img src="'+h+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">Link</span></strong>: Transmits the movement from the Jack to the jaws.<br><img src="'+d+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">Circlip</span></strong>: Axially holds the pins to prevent misalignment.<br><img src="'+_+'" alt=""></p></li><li><p><strong><span style="color:#5DADE2;">Jaws</span></strong>: The movable parts of the gripper, responsible for grasping objects. <img src="'+b+'" alt=""></p></li></ul><blockquote><p><strong>Note:</strong> All parts are available in the provided ZIP file. Make sure to import them into SolidWorks before starting.<br> These can be found in the various files after decompressing the zip file.</p></blockquote><hr><h2 id="assembly-steps" tabindex="-1"><span style="color:rgb(231, 60, 137);"><strong>Assembly Steps</strong></span> <a class="header-anchor" href="#assembly-steps" aria-label="Permalink to &quot;&lt;span style=&quot;color:rgb(231, 60, 137);&quot;&gt;**Assembly Steps**&lt;/span&gt;&quot;">​</a></h2><h3 id="_1-open-the-parts-in-solidworks" tabindex="-1"><span style="color:rgba(13, 176, 81, 0.7);"><strong>1. Open the parts in SolidWorks</strong></span> <a class="header-anchor" href="#_1-open-the-parts-in-solidworks" aria-label="Permalink to &quot;&lt;span style=&quot;color:rgba(13, 176, 81, 0.7);&quot;&gt;**1. Open the parts in SolidWorks**&lt;/span&gt;&quot;">​</a></h3><p>To begin the assembly, it is necessary to open the files in SolidWorks. Once the interface is launched, click on the &quot;Open&quot; icon and select the file “Gripper Assembly”.</p><p><img src="'+y+'" alt=""></p><p><img src="'+u+'" alt=""></p><p>This file is already configured as an assembly (.sldasm), which means there is no need to create a new assembly manually. The Jack is already fixed to the body, but the other necessary components still need to be added.</p><p>For this, use the <strong>&quot;Insert Components&quot;</strong> option in the top toolbar, then select the available parts from the decompressed folder.</p><p><img src="'+k+'" alt=""></p><p><img src="'+f+'" alt=""></p><p>Geometric constraints, such as coaxiality and coincidence, will be essential for correctly aligning the elements. For example, to align two cylindrical surfaces, apply a coaxiality constraint, then use a coincidence constraint to fix the contacting faces.</p><hr><h3 id="fixing-the-jack" tabindex="-1"><span style="color:#28B463;"><strong>Fixing the Jack</strong></span> <a class="header-anchor" href="#fixing-the-jack" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Fixing the Jack**&lt;/span&gt;&quot;">​</a></h3><p>Fixing the Jack is a crucial step, as it ensures that the movement transmitted by the Jack will be correctly relayed to the other components. In the diagram, it can be seen that the rod of the Jack is connected to the end piece via an M5x25 socket head screw. To make this connection, start by aligning the axis of the screw with the cylindrical cavities of the end piece and the Jack rod.</p><p><img src="'+w+'" alt=""></p><p><strong>Methodology:</strong></p><ol><li><p><strong>Select the Constraint tab</strong></p><p><img src="'+D+'" alt=""></p></li><li><p><strong>Select coaxiality</strong> and then click on the cylindrical faces of the cavity and the screw.</p><p><img src="'+t+'" alt=""></p></li><li><p><strong>Select the coincidence constraint</strong> between the base face of the screw cap and the first drilled surface of the cavity.<br><img src="'+T+'" alt=""></p><p><img src="'+t+'" alt=""></p></li></ol><hr><h3 id="assembly-of-the-links" tabindex="-1"><span style="color:#28B463;"><strong>Assembly of the Links</strong></span> <a class="header-anchor" href="#assembly-of-the-links" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Assembly of the Links**&lt;/span&gt;&quot;">​</a></h3><p>The links play a central role in transmitting the movement from the Jack to the jaws. Each end of the Jack is connected to a pair of links, forming a sandwich-like configuration. The links are held in place by the link pins and circlips, which prevent any dislocation of the assembly.</p><p><img src="'+q+'" alt=""></p><p><strong>Steps:</strong></p><ol><li><p>Fix one link on one of the axial ends of the Jack end.</p><p><img src="'+E+'" alt=""></p></li><li><p>Apply a <strong>coaxiality constraint</strong> between the extreme axial cavities of the link and the end piece.</p><p><img src="'+x+'" alt=""></p></li><li><p>Apply a <strong>coincidence constraint</strong> between the opposite faces of the link and the end piece.</p><p><img src="'+A+'" alt=""></p></li><li><p>Secure the assembly with the pins and circlips. <img src="'+v+'" alt=""></p><p><img src="'+j+'" alt=""></p></li><li><p>Repeat the process for the second end of the Jack.</p><p><img src="'+S+'" alt=""></p></li><li><p>Apply a <strong>parallelism constraint</strong> between the opposite faces of the links.</p><p><img src="'+J+'" alt=""></p></li></ol><hr><h3 id="mounting-the-jaw-holders" tabindex="-1"><span style="color:#28B463;"><strong>Mounting the Jaw Holders</strong></span> <a class="header-anchor" href="#mounting-the-jaw-holders" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Mounting the Jaw Holders**&lt;/span&gt;&quot;">​</a></h3><p>The jaw holders consist of two rotation axes: let&#39;s name them A1, located at the rounded end, and A2, located at the elongated end. Axis A1 is directly fixed between the links via their pins, while A2 is fixed to the main body via jaw holder pins and circlips.</p><p><img src="'+P+'" alt=""></p><p><img src="'+B+'" alt=""></p><p><strong>Steps:</strong></p><ol><li><p>Align A1 and A2 on the same axis as their respective cavities.</p><p><img src="'+M+'" alt=""></p></li><li><p>Apply a <strong>coincidence constraint</strong> between A1 and one of the faces of the links.</p><p><img src="'+C+'" alt=""></p></li><li><p>Secure the pins and circlips.</p><p><img src="'+G+'" alt=""></p></li></ol><hr><h3 id="mounting-the-jaws" tabindex="-1"><span style="color:#28B463;"><strong>Mounting the Jaws</strong></span> <a class="header-anchor" href="#mounting-the-jaws" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Mounting the Jaws**&lt;/span&gt;&quot;">​</a></h3><p>The jaws are fixed on the truncated face of the jaw holders at the elongated end and are held in place by two pins to ensure strength and durability.</p><p><img src="'+L+'" alt=""></p><p><img src="'+I+'" alt=""></p><p><strong>Steps:</strong></p><ol><li><p>Apply a <strong>coincidence constraint</strong> between the contact surfaces of the jaws and the jaw holders.</p><p><img src="'+F+'" alt=""></p></li><li><p>Use a <strong>coaxiality constraint</strong> to align the cylindrical cavities of the jaws with the corresponding pins.<br><img src="'+N+'" alt=""></p></li><li><p>Secure each cavity with M5x16 socket head screws.<br><img src="'+O+'" alt=""></p><p><img src="'+W+'" alt=""></p></li></ol><hr><h3 id="final-assembly" tabindex="-1"><span style="color:#28B463;"><strong>Final Assembly</strong></span> <a class="header-anchor" href="#final-assembly" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Final Assembly**&lt;/span&gt;&quot;">​</a></h3><p>In the end, we obtain the sketch of a mechanical gripper capable of grasping and releasing objects.<br><img src="'+H+'" alt=""></p><hr><h2 id="additional-tips" tabindex="-1"><span style="color:rgb(231, 60, 137);"><strong>Additional Tips</strong></span> <a class="header-anchor" href="#additional-tips" aria-label="Permalink to &quot;&lt;span style=&quot;color:rgb(231, 60, 137);&quot;&gt;**Additional Tips**&lt;/span&gt;&quot;">​</a></h2><h3 id="application-of-the-symmetry-constraint" tabindex="-1"><span style="color:#28B463;"><strong>Application of the Symmetry Constraint</strong></span> <a class="header-anchor" href="#application-of-the-symmetry-constraint" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Application of the Symmetry Constraint**&lt;/span&gt;&quot;">​</a></h3><p>To ensure synchronized movement of the links or jaw holders, apply a <strong>symmetry constraint</strong> between the opposite faces of the links.</p><p><img src="'+V+'" alt=""></p><ol><li><p>Select the <strong>Symmetry</strong> constraint under the advanced options.</p><p><img src="'+$+'" alt=""></p></li><li><p>Select the top plane as the axis of symmetry.</p><p><img src="'+Z+'" alt=""></p></li><li><p>Apply the constraint between the opposite faces.</p><p><img src="'+z+'" alt=""></p></li></ol><hr><h3 id="determining-the-center-of-gravity" tabindex="-1"><span style="color:#28B463;"><strong>Determining the Center of Gravity</strong></span> <a class="header-anchor" href="#determining-the-center-of-gravity" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Determining the Center of Gravity**&lt;/span&gt;&quot;">​</a></h3><p>To determine the center of gravity, go to the <strong>Evaluate</strong> tab at the top left and select the <strong>Mass Properties</strong> option.</p><p><img src="'+K+'" alt=""></p><p>The coordinates of the center of gravity are:</p><ul><li><strong>(X = -30.29, Y = 0.0, Z = 24.50)</strong> at its maximum position.</li><li><strong>(X = -45.29, Y = 0.00, Z = 24.50)</strong> at its minimum position.</li></ul><p><img src="'+R+'" alt=""></p><hr><h2 id="errors-to-avoid-during-assembly" tabindex="-1"><span style="color:rgb(231, 60, 137);"><strong>Errors to Avoid During Assembly</strong></span> <a class="header-anchor" href="#errors-to-avoid-during-assembly" aria-label="Permalink to &quot;&lt;span style=&quot;color:rgb(231, 60, 137);&quot;&gt;**Errors to Avoid During Assembly**&lt;/span&gt;&quot;">​</a></h2><ul><li><p><strong>Incorrect orientation of parts</strong> can lead to unexpected effects.</p><p><img src="'+X+'" alt=""></p><p><img src="'+Y+'" alt=""></p><p><img src="'+U+'" alt=""></p></li><li><p><strong>Confusion of screws.</strong></p></li><li><p><strong>Ensure that you have used all the parts.</strong></p></li></ul>',68)]))}const ce=s(Q,[["render",ee]]);export{re as __pageData,ce as default};
