import{_ as t,c as i,o,ag as s}from"./chunks/framework.C_7JoTpM.js";const a="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/original_plan.png",n="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/base.png",r="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/front_plane.png",l="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/line_icon.png",c="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/base_form_drawing.png",p="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/smart_cotation.png",m="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/dimentioning.png",g="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/dimentioning_2.png",h="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/dimentioning_3.png",u="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/dimentioning_4.png",d="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/trapezoid.png",f="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/offset_entity_icon.png",_="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/offset_dimentionning.png",w="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/extruded_boss_icon.png",b="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/extruded_boss.jpeg",k="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/extruded_boss_dimentionning.png",D="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/extruded_boss_dimentionning_view.jpeg",T="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/rectangular_prism.jpeg",q="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/triangula_cut.png",x="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/top_face.jpeg",y="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/current_figure.jpeg",E="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/top_face_1.jpeg",P="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/top_face_2.jpeg",C="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/top_face_3.jpeg",S="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/top_face_4.png",F="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/top_face_5.jpeg",I="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/central_rectangular_cutout.png",v="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/central_rectangular_cutout_2.jpeg",z="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/extruded_cut.png",A="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/cotation_setting.png",j="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/extrusion_result_1.jpeg",M="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/rectangular_cutout.jpeg",R="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/rectangular_cutout_result.jpeg",B="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/material_side.png",H="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/edit_material.png",O="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/edit_material_2.png",U="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/mass_properties.png",W="/2025-Team-Epibot-Docs/images/mechanic_images/week1/piece3/mass_value.png",N="/2025-Team-Epibot-Docs/videos/mechanic_videos/week1/piece3_simulation.mp4",Q=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"week1/pole-mecanic/Piece3.md","filePath":"week1/pole-mecanic/Piece3.md"}'),V={name:"week1/pole-mecanic/Piece3.md"};function $(L,e,Y,G,J,K){return o(),i("div",null,e[0]||(e[0]=[s('<h2 id="documentation-of-the-construction-of-piece-iii" tabindex="-1"><span style="color:#949CDF;font-size:28px;font-weight:bold;"><em><strong>Documentation of the Construction of piece III</strong></em></span> <a class="header-anchor" href="#documentation-of-the-construction-of-piece-iii" aria-label="Permalink to &quot;&lt;span style=&quot;color: #949CDF; font-size: 28px; font-weight: bold;&quot;&gt;***Documentation of the Construction of piece III***&lt;/span&gt;&quot;">​</a></h2><p>Part III, designed by <code>TEKBOT</code> as part of <em><strong>the TRC Urban Resilience</strong></em> project, is an AISI 1020 steel structure intended to serve as a support or connection in mechanical or architectural assemblies. Its geometry is based on an assembly of prismatic square-section profiles, designed to ensure rigidity and lightness. The different faces of the part—plan representation, isometric view, and section— allow a complete understanding of the shape and critical dimensions before manufacturing.</p><h2 id="piece-to-be-made" tabindex="-1"><span style="color:#949CDF;font-size:28px;font-weight:bold;"><strong>Piece to be made</strong></span> <a class="header-anchor" href="#piece-to-be-made" aria-label="Permalink to &quot;&lt;span style=&quot;color: #949CDF; font-size: 28px; font-weight: bold;&quot;&gt;**Piece to be made**&lt;/span&gt;&quot;">​</a></h2><p><img src="'+a+'" alt=""></p><p>Here is a step-by-step guide to making this piece:</p><h2 id="step-one-creating-the-solid-base" tabindex="-1"><span style="color:#949CDF;font-size:28px;font-weight:bold;">Step One : Creating the Solid Base</span> <a class="header-anchor" href="#step-one-creating-the-solid-base" aria-label="Permalink to &quot;&lt;span style=&quot;color: #949CDF; font-size: 28px; font-weight: bold;&quot;&gt;Step One : Creating the Solid Base&lt;/span&gt;&quot;">​</a></h2><p><img src="'+n+'" alt=""></p><h3 id="description" tabindex="-1"><strong><code>Description</code></strong> <a class="header-anchor" href="#description" aria-label="Permalink to &quot;**`Description`**&quot;">​</a></h3><p>This step shows an isosceles trapezoid and is the very first to be performed, ensuring a good foundation for our piece. To do this, do the following:</p><p><strong>Actions</strong></p><ul><li>Selection of the XY plane, particularly called Front Plane in SolidWorks.</li></ul><p><img src="'+r+'" alt=""></p><ul><li>Drawing a 2D rectangle with the following dimensions :</li><li>Length : <strong>150,00 mm</strong></li><li>Width : <strong>70,00 mm</strong><em><strong>How to achieve this?</strong></em></li></ul><p>Follow these steps:</p><ul><li>Click on then in the window that opens choose the <strong>line option</strong></li></ul><p><img src="'+l+'" alt=""></p><ul><li>Position your cursor at the origin of your reference point then represent the 2D rectangle without taking into account the dimensions as follows :</li></ul><p><img src="'+c+'" alt=""></p><ul><li>After that we will assign the dimensions. To do this, click on Smart Dimension to automate the dimensions :</li></ul><p><img src="'+p+'" alt=""></p><p>This function will allow you to automate the measurement of dimensions by modifying them yourself; you just need to click on the line you want to resize, hold the click in the desired direction.</p><p><em><strong>Illustration</strong></em></p><p><img src="'+m+'" alt=""> <img src="'+g+'" alt=""><img src="'+h+'" alt=""></p><ul><li>The dimensions therefore comply with the requirements.Mesures</li><li>Trapezoid length : <strong>150,00 mm</strong></li><li>Trapezoid width : <strong>70,00 mm</strong></li></ul><h3 id="result" tabindex="-1"><strong><code>Result</code></strong> <a class="header-anchor" href="#result" aria-label="Permalink to &quot;**`Result`**&quot;">​</a></h3><p>A 2D rectangle with dimensions 150.00 mm × 70.00 mm is created, representing the base of the part.</p><p><img src="'+u+'" alt=""></p><h2 id="step-two-extruding-the-base-to-form-a-prism" tabindex="-1"><span style="color:#949CDF;font-size:28px;font-weight:bold;">Step Two : Extruding the Base to Form a Prism</span> <a class="header-anchor" href="#step-two-extruding-the-base-to-form-a-prism" aria-label="Permalink to &quot;&lt;span style=&quot;color: #949CDF; font-size: 28px; font-weight: bold;&quot;&gt;Step Two : Extruding the Base to Form a Prism&lt;/span&gt;&quot;">​</a></h2><h3 id="description-1" tabindex="-1"><strong><code>Description</code></strong> <a class="header-anchor" href="#description-1" aria-label="Permalink to &quot;**`Description`**&quot;">​</a></h3><p>Transformation of the 2D trapezoid into a 3D solid by extruding it to give it a height, corresponding to the total height of the initial part.</p><p><strong>How to do it ?</strong></p><p>Actions</p><ul><li>Selection of the current 2D trapezoid.</li></ul><p><img src="'+d+'" alt=""></p><ul><li>Using the Offset Entities tool to double your trapezoid.</li></ul><p><img src="'+f+'" alt=""></p><ul><li>Set the extrusion height to 10.00 mm.</li></ul><p><img src="'+_+'" alt=""></p><ul><li>Using the extrusion tool</li></ul><p><img src="'+w+'" alt=""></p><ul><li><p>Select the current figure then click on the function <strong>Extruded Boss/Base</strong>. You will get an interface like this :</p><p><img src="'+b+'" alt=""></p></li><li><p>Change the value according to the requirements. In our case we must use 100.00 mm</p></li></ul><p><img src="'+k+'" alt=""></p><ul><li>After this modification you will get a figure like this:</li></ul><p><img src="'+D+'" alt=""></p><h3 id="measures" tabindex="-1"><strong><code>Measures</code></strong> <a class="header-anchor" href="#measures" aria-label="Permalink to &quot;**`Measures`**&quot;">​</a></h3><ul><li>Prism length : <strong>150,00 mm</strong></li><li>Prism width : <strong>70,00 mm</strong></li><li>Prism height : <strong>10,00 mm</strong></li></ul><h3 id="result-1" tabindex="-1"><strong><code>Result</code></strong> <a class="header-anchor" href="#result-1" aria-label="Permalink to &quot;**`Result`**&quot;">​</a></h3><p>A rectangular prism with dimensions 150.00 mm × 70.00 mm × 100.00 mm is created, representing the initial gross volume of the part.</p><p><img src="'+T+'" alt=""></p><h2 id="step-three-triangular-cut-on-the-upper-face" tabindex="-1"><span style="color:#949CDF;font-size:28px;font-weight:bold;">Step Three : Triangular Cut on the Upper Face </span> <a class="header-anchor" href="#step-three-triangular-cut-on-the-upper-face" aria-label="Permalink to &quot;&lt;span style=&quot;color: #949CDF; font-size: 28px; font-weight: bold;&quot;&gt;Step Three : Triangular Cut on the Upper Face &lt;/span&gt;&quot;">​</a></h2><p><img src="'+q+'" alt=""></p><h3 id="description-2" tabindex="-1"><strong><code>Description</code></strong> <a class="header-anchor" href="#description-2" aria-label="Permalink to &quot;**`Description`**&quot;">​</a></h3><p>At this stage, you need to add a triangular cutout on the upper face of the prism.Comment s’y prendre ?</p><p><strong>Actions</strong></p><ul><li><p>In a Front Plane, select the top face of the prism (150.00 mm × 70.00 mm). In this illustration, the top face of the prism is the one in blue.</p><p><img src="'+x+'" alt=""></p></li><li><p>Drawing a 2D triangle on this face :</p></li><li><p>Cutting length : <strong>100,00 mm</strong></p></li><li><p>Cutting width : <strong>60,00 mm</strong></p></li><li><p>Cutting depth : <strong>20,00 mm</strong></p><p>Let&#39;s do it, step by step:</p></li><li><p>First select the current figure :</p></li></ul><p><img src="'+y+'" alt=""></p><ul><li><p>Select the line function to perform the drawing</p></li><li><p>Draw the horizontal line (100.00 mm)</p><p><img src="'+E+'" alt=""></p></li><li><p>Draw the vertical line (20.00 mm)</p><p><img src="'+P+'" alt=""></p></li><li><p>Draw the second horizontal line in the image of the first We obtain the following :</p><p><img src="'+C+'" alt=""></p></li><li><p>Next, we&#39;ll apply the Cutout Depth: 20.00 mm</p><p>Use SolidWorks&#39; Smart Dimension feature once again to automate dimension measurement.</p><p><img src="'+S+'" alt=""></p></li></ul><p>Result</p><p>The prism has a triangular cutout on its upper face, leaving a 20.00 mm border on each side along the width.</p><p><img src="'+F+'" alt=""></p><h2 id="step-four-central-rectangular-cutout" tabindex="-1"><span style="color:#949CDF;font-size:28px;font-weight:bold;">Step Four : Central Rectangular Cutout</span> <a class="header-anchor" href="#step-four-central-rectangular-cutout" aria-label="Permalink to &quot;&lt;span style=&quot;color: #949CDF; font-size: 28px; font-weight: bold;&quot;&gt;Step Four : Central Rectangular Cutout&lt;/span&gt;&quot;">​</a></h2><p><img src="'+I+'" alt=""></p><h3 id="description-3" tabindex="-1"><strong><code>Description</code></strong> <a class="header-anchor" href="#description-3" aria-label="Permalink to &quot;**`Description`**&quot;">​</a></h3><p>At this stage, you need to add a central rectangular cutout.</p><p><strong>How to do it?</strong></p><p>Actions</p><ul><li>Selection of the upper face of the prism, in the area defined by the triangular cutout.</li></ul><p><img src="'+v+'" alt=""></p><ul><li>Using the extrusion cutting tool to remove this triangular shape to a depth of 100.00 mm.</li><li>Selects the extrusion cutting functionality</li></ul><p><img src="'+z+'" alt=""></p><ul><li>Changes the depth of this extrusion (100.00 mm)</li></ul><p><img src="'+A+'" alt=""></p><p>You will get a figure like this:</p><p><img src="'+j+'" alt=""></p><p>After validating your extrusion you will obtain the final result.</p><h3 id="measures-1" tabindex="-1"><strong><code>Measures</code></strong> <a class="header-anchor" href="#measures-1" aria-label="Permalink to &quot;**`Measures`**&quot;">​</a></h3><ul><li>Cutout Length : <strong>100,00 mm</strong></li><li>Cutout Width : <strong>60,00 mm</strong></li><li>Cutout Depth : <strong>20,00 mm</strong></li></ul><h3 id="result-2" tabindex="-1"><strong><code>Result</code></strong> <a class="header-anchor" href="#result-2" aria-label="Permalink to &quot;**`Result`**&quot;">​</a></h3><p>A rectangular cutout of 100.00 mm × 60.00 mm × 20.00 mm is added to the center of the prism.</p><p><img src="'+M+'" alt=""></p><h3 id="step-five-final-adjustments-and-penrose-triangle-transformation" tabindex="-1"><span style="color:rgb(6, 91, 249);background:#E6F4EA;padding:4px 8px;border-radius:4px;">Step Five : Final Adjustments and Penrose Triangle Transformation</span> <a class="header-anchor" href="#step-five-final-adjustments-and-penrose-triangle-transformation" aria-label="Permalink to &quot;&lt;span style=&quot;color: rgb(6, 91, 249); background: #E6F4EA; padding: 4px 8px; border-radius: 4px;&quot;&gt;Step Five : Final Adjustments and Penrose Triangle Transformation&lt;/span&gt;&quot;">​</a></h3><h3 id="description-4" tabindex="-1"><strong><code>Description</code></strong> <a class="header-anchor" href="#description-4" aria-label="Permalink to &quot;**`Description`**&quot;">​</a></h3><p>This step is the final one, showing the solid in its final phase. The part is manufactured from AISI 1020 steel (density: 0.0079 g/mm³), using the MMGS system and an accuracy of two decimal places. AISI 1020 offers a good compromise between strength, ductility, and cost, ideal for this type of component. Density is specified to calculate weight, a key factor in design and transport. Precision and the MMGS system ensure uniform and reliable manufacturing.</p><p><strong>Final result</strong></p><p><img src="'+R+'" alt=""></p><h2 id="how-to-change-the-appearance-of-the-part-to-aisi-1020" tabindex="-1"><span style="color:#949CDF;font-size:28px;font-weight:bold;"><strong>How to change the appearance of the part to AISI 1020?</strong></span> <a class="header-anchor" href="#how-to-change-the-appearance-of-the-part-to-aisi-1020" aria-label="Permalink to &quot;&lt;span style=&quot;color: #949CDF; font-size: 28px; font-weight: bold;&quot;&gt;**How to change the appearance of the part to AISI 1020?** &lt;/span&gt;&quot;">​</a></h2><p>Follow these steps :</p><ul><li>On the interface <em><strong>SolidWorks</strong></em> click on <em><strong>Material not specified</strong></em> then a window will open</li></ul><p><img src="'+B+'" alt=""></p><ul><li>This is what this window looks like, choose the option <em><strong>Edit Material</strong></em> then click on it and a window will open</li></ul><p><img src="'+H+'" alt=""></p><ul><li>This is what this window looks like. In the list of materials, choose the one required in our case, it will be AISI 1020</li></ul><p><img src="'+O+'" alt=""></p><p>In this case it is not necessary to modify the density ; it cannot be modified ; its value remains and remains at : 0.0079 g/mm³.</p><h2 id="how-to-calculate-the-mass-of-this-solid" tabindex="-1"><span style="color:#949CDF;font-size:28px;font-weight:bold;"><strong>How to calculate the mass of this solid ?</strong></span> <a class="header-anchor" href="#how-to-calculate-the-mass-of-this-solid" aria-label="Permalink to &quot;&lt;span style=&quot;color: #949CDF; font-size: 28px; font-weight: bold;&quot;&gt;**How to calculate the mass of this solid ?**&lt;/span&gt;&quot;">​</a></h2><p>Follow these steps :</p><ul><li>On the SolidWorks interface click on the <em><strong>Mass Properties</strong></em> feature click on it and a window will open</li></ul><p><img src="'+U+'" alt=""></p><ul><li><p>This is what this window looks like. In this window you will see the calculations made including the mass of the solid in our case the mass of the part is as follows <strong>: Mass = 1181.60 grams</strong></p><p><img src="'+W+'" alt=""></p></li></ul><hr><h3 id="short-demo" tabindex="-1"><span style="color:#28B463;"><strong>Short Demo</strong></span> <a class="header-anchor" href="#short-demo" aria-label="Permalink to &quot;&lt;span style=&quot;color: #28B463;&quot;&gt;**Short Demo**&lt;/span&gt;&quot;">​</a></h3><video width="320" height="240" controls><source src="'+N+'" type="video/mp4"> Votre navigateur ne supporte pas la balise vidéo. </video><hr>',103)]))}const Z=t(V,[["render",$]]);export{Q as __pageData,Z as default};
