import{_ as t,a as o,b as a,c as r,d as s,e as n,f as i,g as c}from"./chunks/conveyor3.Bz1hLLY-.js";import{_ as l,c as g,o as m,ag as d}from"./chunks/framework.C_7JoTpM.js";const v=JSON.parse('{"title":"FINAL TEST - THE CONVEYOR: Documentation","description":"","frontmatter":{},"headers":[],"relativePath":"week4/index.md","filePath":"week4/index.md"}'),p={name:"week4/index.md"};function h(u,e,_,b,f,T){return m(),g("div",null,e[0]||(e[0]=[d('<h1 id="final-test-the-conveyor-documentation" tabindex="-1">FINAL TEST - THE CONVEYOR: Documentation <a class="header-anchor" href="#final-test-the-conveyor-documentation" aria-label="Permalink to &quot;FINAL TEST - THE CONVEYOR: Documentation&quot;">​</a></h1><h2 id="summary" tabindex="-1">Summary <a class="header-anchor" href="#summary" aria-label="Permalink to &quot;Summary&quot;">​</a></h2><ul><li><a href="#1-general-context">1. General Context</a></li><li><a href="#2-test-objective">2. Test Objective</a></li></ul><h2 id="_1-general-context" tabindex="-1">1. General Context <a class="header-anchor" href="#_1-general-context" aria-label="Permalink to &quot;1. General Context&quot;">​</a></h2><p>This is the final selection test of the <strong>Tekbot Robotics Challenge 2025</strong>. It is a <strong>multidisciplinary challenge</strong> involving the different divisions: <strong>mechanical</strong>, <strong>electronic</strong>, and <strong>computer science</strong>.</p><h2 id="_2-test-objective" tabindex="-1">2. Test Objective <a class="header-anchor" href="#_2-test-objective" aria-label="Permalink to &quot;2. Test Objective&quot;">​</a></h2><p>Design an <strong>intelligent conveyor system</strong> capable of automatically sorting four types of waste, represented by <strong>colored cubes</strong> (green, yellow, red, blue).<br> The system must detect waste, activate the conveyor belt, and display the sorting results <strong>in real time</strong> on a dedicated <strong>web interface</strong>.</p><h3 id="how-we-designed-our-conveyor" tabindex="-1">How we designed our conveyor: <a class="header-anchor" href="#how-we-designed-our-conveyor" aria-label="Permalink to &quot;How we designed our conveyor:&quot;">​</a></h3><ul><li>The conveyor remains <strong>stationary</strong> until waste is detected.</li><li>The waste is then <strong>identified by color</strong> using a <strong>color sensor</strong>.</li><li>The detected color is then <strong>sent to the web platform</strong>.</li><li>A <strong>web interface</strong> allows <strong>real-time monitoring</strong> of the quantity of sorted waste.</li></ul><p><a href="/2025-Team-Epibot-Docs/week4/images/pcb1.jpeg"><img src="'+t+'" alt="Vercel Homepage"></a><a href="/2025-Team-Epibot-Docs/week4/images/pcb2.jpeg"><img src="'+o+'" alt="Vercel Homepage"></a><a href="/2025-Team-Epibot-Docs/week4/images/pcb3.jpeg"><img src="'+a+'" alt="Vercel Homepage"></a><a href="/2025-Team-Epibot-Docs/week4/images/pcb4.jpeg"><img src="'+r+'" alt="Vercel Homepage"></a><a href="/2025-Team-Epibot-Docs/week4/images/convenyor.jpeg"><img src="'+s+'" alt="Vercel Homepage"></a><a href="/2025-Team-Epibot-Docs/week4/images/conveyor1.jpeg"><img src="'+n+'" alt="Vercel Homepage"></a><a href="/2025-Team-Epibot-Docs/week4/images/conveyor2.jpeg"><img src="'+i+'" alt="Vercel Homepage"></a><a href="/2025-Team-Epibot-Docs/week4/images/conveyor3.jpeg"><img src="'+c+'" alt="Vercel Homepage"></a></p>',10)]))}const k=l(p,[["render",h]]);export{v as __pageData,k as default};
